mapper.select:
	select count(*)
    from tbl_activity
    where fk_userid = #{userid}

select trunc(sysdate-update_passwd_date)
from tbl_member_login
where userid = #{userid}

select V.* from (
select DENSE_RANK() OVER (ORDER BY C.board_num desc) rno
select DENSE_RANK() OVER (ORDER BY C.${sort} desc,C.board_num desc) rno
,C.board_num ,C.nickname ,C.category ,C.detail_category ,C.subject ,C.write_date ,C.views ,C.profile_image ,C.comment_cnt ,C.like_cnt ,C.hashtag_num ,C.hashtag ,C.fk_board_num ,D.point writer_point from ( select A.board_num,A.nickname,A.category,A.detail_category,A.subject,A.write_date,A.views,A.profile_image,A.comment_cnt,A.Like_cnt ,B.hashtag_num,B.fk_board_num,B.hashtag from ( select board_num,nickname,category,detail_category,subject,write_date,views,profile_image,comment_cnt,Like_cnt from tbl_board where category = '공지사항'
and subject like '%'|| lower(#{searchWord}) ||'%'
) A left join (select a.hashtag_num, hashtag, fk_board_num from tbl_hashtag a join tbl_hashtag_board_map b on a.hashtag_num = b.hashtag_num) B on A.board_num = B.fk_board_num ) C left join (select point,nickname from tbl_member) D on C.nickname = D.nickname ) V where rno between #{startRno} and #{endRno}

select count(*)
from tbl_board
where category = #{category} and detail_category = #{detail_category}

select V.* from (
select DENSE_RANK() OVER (ORDER BY C.board_num desc) rno
select DENSE_RANK() OVER (ORDER BY C.${sort} desc,C.board_num desc) rno
,C.board_num ,C.nickname ,C.category ,C.detail_category ,C.subject ,C.write_date ,C.views ,C.profile_image ,C.comment_cnt ,C.like_cnt ,C.hashtag_num ,C.hashtag ,C.fk_board_num ,C.representative_name ,C.address ,C.phone ,C.jurisdiction ,C.homepage ,C.academy_image ,D.point writer_point from ( select A.board_num,A.nickname,A.category,A.detail_category,A.subject,A.write_date,A.views,A.profile_image,A.comment_cnt,A.Like_cnt ,B.hashtag_num,B.hashtag ,E.* from ( select board_num,nickname,category,detail_category,subject,write_date,views,profile_image,comment_cnt,Like_cnt from tbl_board where category = #{category} and detail_category = #{detail_category}
and subject like '%'|| lower(#{searchWord}) ||'%'
) A left join (select a.hashtag_num, hashtag, fk_board_num from tbl_hashtag a join tbl_hashtag_board_map b on a.hashtag_num = b.hashtag_num) B on A.board_num = B.fk_board_num left join (select * from tbl_academy) E on A.board_num = E.fk_board_num ) C left join (select point,nickname from tbl_member) D on C.nickname = D.nickname ) V where rno between #{startRno} and #{endRno}

select detail_category
from tbl_board
where board_num = #{board_num}

select count(*)
from tbl_board
where category='국비학원' and detail_category = #{detail_category}

select V.* from (
select DENSE_RANK() OVER (ORDER BY C.board_num desc) rno
select DENSE_RANK() OVER (ORDER BY C.${sort} desc,C.board_num desc) rno
,C.board_num ,C.nickname ,C.category ,C.detail_category ,C.subject ,C.write_date ,C.views ,C.profile_image ,C.comment_cnt ,C.like_cnt ,C.hashtag_num ,C.hashtag ,C.fk_board_num ,C.core_technology ,C.academy_name ,C.curriculum_start_date ,C.curriculum_end_date from ( select A.board_num,A.nickname,A.category,A.detail_category,A.subject,A.write_date,A.views,A.profile_image,A.comment_cnt,A.Like_cnt ,B.hashtag_num,B.hashtag ,E.* from ( select board_num,nickname,category,detail_category,subject,write_date,views,profile_image,comment_cnt,Like_cnt from tbl_board where category = #{category} and detail_category = #{detail_category}
and subject like '%'|| lower(#{searchWord}) ||'%'
) A left join (select a.hashtag_num, hashtag, fk_board_num from tbl_hashtag a join tbl_hashtag_board_map b on a.hashtag_num = b.hashtag_num) B on A.board_num = B.fk_board_num left join (select * from tbl_curriculum) E on A.board_num = E.fk_board_num ) C left join (select point,nickname from tbl_member) D on C.nickname = D.nickname ) V where rno between #{startRno} and #{endRno}
