mapper.select:
select T.board_num,T.nickname,T.category,T.detail_category,T.subject,T.content ,T.write_date,T.views,T.profile_image,T.comment_cnt,T.like_cnt,T.hashtag_num ,T.hashtag,T.writer_point,T.previousseq,T.previoussubject, T.nextseq, T.nextsubject, m.content comment_content , m.comment_num from ( select * from ( select C.board_num ,C.nickname ,C.category ,C.detail_category ,C.subject ,C.content ,C.write_date ,C.views ,C.profile_image ,C.comment_cnt ,C.like_cnt ,C.hashtag_num ,C.hashtag ,C.fk_board_num ,D.point writer_point ,C.previousseq ,C.previoussubject ,C.nextseq ,C.nextsubject
from ( select lag(A.board_num, 1) over(order by A.board_num desc) AS previousseq , lag(A.subject, 1) over(order by A.board_num desc) AS previoussubject , lead(A.board_num, 1) over(order by A.board_num desc) AS nextseq , lead(A.subject, 1) over(order by A.board_num desc) AS nextsubject ,A.board_num,A.nickname,A.category,A.detail_category,A.subject,A.content,A.write_date,A.views,A.profile_image,A.comment_cnt,A.Like_cnt ,hashtag_num,fk_board_num,hashtag from (select * from tbl_board
) A left join (select a.hashtag_num, hashtag, fk_board_num from tbl_hashtag a join tbl_hashtag_board_map b on a.hashtag_num = b.hashtag_num) h on A.board_num = h.fk_board_num ) C left join (select point,nickname from tbl_member) D on C.nickname = D.nickname ) V
) T left join (select comment_num,content,board_num from tbl_comment) m on t.board_num = m.board_num where t.board_num = #{board_num}

select detail_category
from tbl_board
where board_num = #{board_num}

select count(*)
from tbl_board_like
where fk_board_num = #{board_num} and fk_userid = #{userid}

select like_cnt
from tbl_board
where board_num=#{board_num}

	select fk_userid,fk_board_num
    from tbl_board_like
    where fk_userid = #{userid} and fk_board_num = #{str_board_num}
