mapper.select:
select count(*) from tbl_member M left join tbl_member_login L on fk_userid = userid where 1 = 1
and status = #{memberStatus}
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'

select rnum, userid, email, email_acept, nickname, point, join_date , profile_image, username, kakao, naver, google, facebook, authority , academy_name, company_num, homepage, tel, status from ( select rownum as rnum, userid, email, email_acept, nickname, point, to_char(join_date, 'yyyy-mm-dd') as join_date , profile_image, username, kakao, naver, google, facebook, authority , academy_name, company_num, homepage, tel, status from ( select V.userid as userid, V.email as email, V.email_acept as email_acept, V.nickname as nickname, V.point as point, nvl(join_date, to_char(sysdate-30, 'yyyy-mm-dd')) as join_date , V.profile_image as profile_image, V.username as username, V.kakao as kakao, V.naver as naver, V.google as google, V.facebook as facebook , V.authority as authority, C.academy_name as academy_name, C.company_num as company_num, C.homepage as homepage, C.tel as tel, status from ( select A.userid,email,email_acept,nickname,point,join_date,profile_image,username,kakao,google,naver,facebook, authority, status from ( select userid, status from tbl_member_login )A left join tbl_member B on A.userid = B.fk_userid ) V left join tbl_academy_member C on V.userid = C.fk_userid
where status = #{memberStatus}
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
where 1 = 1
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
order by join_date desc ) D where company_num is null ) E where rnum between #{startRno} and #{endRno}

select count(*) from tbl_academy_member A left join tbl_member_login L on fk_userid = userid where 1 = 1
and status = #{memberStatus}
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'

select rnum, userid, email, email_acept, nickname, point, join_date , profile_image, username, kakao, naver, google, facebook, authority , academy_name, company_num, homepage, tel, status from ( select rownum as rnum, userid, email, email_acept, nickname, point, to_char(join_date, 'yyyy-mm-dd') as join_date , profile_image, username, kakao, naver, google, facebook, authority , academy_name, company_num, homepage, tel, status from ( select V.userid as userid, V.email as email, V.email_acept as email_acept, V.nickname as nickname, V.point as point, nvl(join_date, to_char(sysdate-30, 'yyyy-mm-dd')) as join_date , V.profile_image as profile_image, V.username as username, V.kakao as kakao, V.naver as naver, V.google as google, V.facebook as facebook , V.authority as authority, C.academy_name as academy_name, C.company_num as company_num, C.homepage as homepage, C.tel as tel, status from ( select A.userid,email,email_acept,nickname,point,join_date,profile_image,username,kakao,google,naver,facebook, authority, status from ( select userid, status from tbl_member_login )A left join tbl_member B on A.userid = B.fk_userid ) V left join tbl_academy_member C on V.userid = C.fk_userid
where status = #{memberStatus}
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
where 1 = 1
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
order by join_date desc ) D where tel is not null ) E where rnum between #{startRno} and #{endRno}

