mapper.select:
select count(*) from tbl_advertisement where 1 = 1
and division = #{memberStatus}
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'

select rnum, advertisement_num, division, client_name, client_phone, filename, orgfilename, url, to_char(start_date, 'yyyy-mm-dd') as start_date, period, status, to_char(end_date, 'yyyy-mm-dd') as end_date from ( select rownum as rnum, advertisement_num, division, client_name, client_phone, filename, orgfilename, url, start_date, period, status, (start_date+period) as end_date from tbl_advertisement
where division = #{memberStatus}
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
where 1 = 1
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
order by start_date desc ) V where rnum between #{startRno} and #{endRno}

        select advertisement_num, division, client_name, client_phone, orgfilename, url, to_char(start_date, 'yyyy-mm-dd') as start_date, to_char((start_date+period), 'yyyy-mm-dd') as end_date, period, status, filename, filesize
        from tbl_advertisement       
		where advertisement_num = #{advertisement_num}

mapper.update:
update tbl_advertisement
set start_date =  to_char(to_date(#{start_date}), 'yyyy-mm-dd') 
where advertisement_num = #{advertisement_num}

	update tbl_advertisement
	set period = trunc(to_date(#{end_date}) - start_date) 
    where advertisement_num = #{advertisement_num}

mapper.select:
select count(*) from ( select report_num, fk_board_num as fk_num, report_nickname, reported_nickname, simple_report_reason, detail_report_reason, report_date, report_type from tbl_board_report union all select report_num, fk_comment_num as fk_num, report_nickname, reported_nickname, simple_report_reason, detail_report_reason, report_date, report_type from tbl_comment_report ) where 1 = 1
and report_type = #{memberStatus}
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'

select rnum, report_num, fk_num, report_nickname, reported_nickname, simple_report_reason, detail_report_reason, to_char(report_date, 'yyyy-mm-dd') as report_date, report_type from ( select rownum as rnum, report_num, fk_num, report_nickname, reported_nickname, simple_report_reason, detail_report_reason, report_date, report_type from ( select report_num, fk_board_num as fk_num, report_nickname, reported_nickname, simple_report_reason, detail_report_reason, report_date, report_type from tbl_board_report union all select report_num, fk_comment_num as fk_num, report_nickname, reported_nickname, simple_report_reason, detail_report_reason, report_date, report_type from tbl_comment_report order by report_date desc ) V
where report_type = #{memberStatus}
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
where 1 = 1
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
) D where rnum between #{startRno} and #{endRno}

select fk_userid, nickname
from tbl_member
where nickname = #{reportedNickname}

