		) V        
		) D
		where rnum between #{startRno} and #{endRno}

select count(*) from tbl_penalty where 1 = 1
and simple_penalty_reason = #{memberStatus}
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'

select rnum, penalty_num, nickname, simple_penalty_reason, detail_penalty_reason, penalty_start_date, penalty_period from ( select rownum as rnum, penalty_num, nickname, simple_penalty_reason, detail_penalty_reason, to_char(penalty_start_date, 'yyyy-mm-dd') as penalty_start_date, penalty_period from ( select penalty_num, nickname, simple_penalty_reason, detail_penalty_reason, penalty_start_date, penalty_period from tbl_penalty where 1 = 1
and simple_penalty_reason = #{memberStatus}
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
order by penalty_start_date desc ) A ) B where rnum between #{startRno} and #{endRno}

select penalty_num, nickname, simple_penalty_reason, detail_penalty_reason, penalty_start_date, penalty_period
from tbl_penalty
where penalty_num = #{penalty_num}
