mapper.select:
select count(*) from tbl_member M left join tbl_member_login L on fk_userid = userid where 1 = 1
and status = #{memberStatus}
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'

select rnum, fk_userid as userid, email, email_acept, username, nickname, to_char(join_date, 'yyyy-mm-dd') as join_date, status, point from ( select rownum as rnum, fk_userid, email, email_acept, username, nickname, join_date, status, point from ( select fk_userid, email, email_acept, username, nickname, join_date, status, point from tbl_member M left join tbl_member_login L on fk_userid = userid
where status = #{memberStatus}
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
where 1 = 1
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
order by join_date desc ) V ) D where rnum between #{startRno} and #{endRno}

select count(*) from tbl_academy_member A left join tbl_member_login L on fk_userid = userid where 1 = 1
and status = #{memberStatus}
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'

select rnum, fk_userid as userid, email, email_acept, academy_name, nickname, company_num, username, phone, homepage, to_char(join_date, 'yyyy-mm-dd') as join_date, status, point, profile_image from ( select rownum as rnum, fk_userid, email, email_acept, academy_name, nickname, company_num, username, phone, homepage, join_date, status, point, profile_image from ( select fk_userid, email, email_acept, academy_name, nickname, company_num, username, phone, homepage, join_date, status, point, profile_image from tbl_academy_member M left join tbl_member_login L on fk_userid = userid
where status = #{memberStatus}
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
where 1 = 1
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
order by join_date desc ) V ) D where rnum between #{startRno} and #{endRno}

