mapper.select:
select count(*) from tbl_advertisement where 1 = 1
and division = #{memberStatus}
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'

select rnum, advertisement_num, division, client_name, client_phone, filename, orgfilename, url, to_char(start_date, 'yyyy-mm-dd') as start_date, period, status from ( select rownum as rnum, advertisement_num, division, client_name, client_phone, filename, orgfilename, url, start_date, period, status from tbl_advertisement
where division = #{memberStatus}
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
where 1 = 1
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
order by start_date desc ) V where rnum between #{startRno} and #{endRno}

        select advertisement_num, division, client_name, client_phone, orgfilename, url, to_char(start_date, 'yyyy-mm-dd') as start_date, period, status, filename, filesize
        from tbl_advertisement       
		where advertisement_num = #{advertisement_num}

select count(*) from ( select report_num, fk_board_num as fk_num, report_nickname, reported_nickname, simple_report_reason, detail_report_reason, report_date, report_type from tbl_board_report union all select report_num, fk_comment_num as fk_num, report_nickname, reported_nickname, simple_report_reason, detail_report_reason, report_date, report_type from tbl_comment_report ) where 1 = 1
and report_type = #{memberStatus}
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'

select rnum, report_num, fk_num, report_nickname, reported_nickname, simple_report_reason, detail_report_reason, to_char(report_date, 'yyyy-mm-dd') as report_date, report_type from ( select rownum as rnum, report_num, fk_num, report_nickname, reported_nickname, simple_report_reason, detail_report_reason, report_date, report_type from ( select report_num, fk_board_num as fk_num, report_nickname, reported_nickname, simple_report_reason, detail_report_reason, report_date, report_type from tbl_board_report union all select report_num, fk_comment_num as fk_num, report_nickname, reported_nickname, simple_report_reason, detail_report_reason, report_date, report_type from tbl_comment_report
order by report_date desc ) V
where report_type = #{memberStatus}
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
where 1 = 1
and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
) D where rnum between #{startRno} and #{endRno}

select activity_num, fk_userid, fk_board_num, to_char(activity_date, 'yyyy-mm-dd') as activity_date, subject, detail_category, division, nickname
from tbl_activity 
where fk_userid = #{userid}

select count(*)
from tbl_activity
where fk_userid = #{userid}

  and division = #{memberStatus}


and lower(${searchType}) like '%'||lower(#{searchWord})||'%'

        select rnum, activity_num, fk_userid, fk_board_num, activity_date, subject, detail_category, division, nickname
        from 
        (
        select rownum as rnum, activity_num, fk_userid, fk_board_num, activity_date, subject, detail_category, division, nickname
        from 
        (
        select activity_num, fk_userid, fk_board_num, to_char(activity_date, 'yyyy-mm-dd') as activity_date, subject, detail_category, division, nickname
        from tbl_activity 
        where fk_userid = #{userid}

	      and division = #{memberStatus}

	    	  and lower(${searchType}) like '%'||lower(#{searchWord})||'%'




		    and lower(${searchType}) like '%'||lower(#{searchWord})||'%'


		order by activity_date desc
		) V        
		) D
		where rnum between #{startRno} and #{endRno}
